use std::{
    fs,
    io::{self, Write},
    path::Path,
};
use tokio::time::Duration;

// Use types from the core library
use volition_agent_core::{
    api::chat_with_api, // Import specific function
    config::{load_runtime_config, RuntimeConfig}, // Import specific items
    models::chat::ResponseMessage, // Use re-exported type
    ToolProvider, // Import trait
    Agent, // Import Agent struct (will be used later)
    AgentOutput, // Import AgentOutput struct (will be used later)
};

use crate::models::cli::Cli; // Keep CLI-specific model import
use crate::rendering::print_formatted;
use crate::tools::{handle_tool_calls, CliToolProvider}; // Import the CLI's ToolProvider impl

use clap::Parser;
use reqwest::Client; // Import Client
use std::sync::Arc; // Import Arc
use tracing::{debug, error, info, warn, Level};
use tracing_subscriber::FmtSubscriber;

const RECOVERY_FILE_PATH: &str = ".conversation_state.json";

// --- Helper Functions ---

fn print_welcome_message() {
    println!("\n{}", "Volition - AI Assistant".cyan().bold());
    println!(
        "{}",
        "Type 'exit' or press Enter on an empty line to quit.".cyan()
    );
    println!();
}

/// Attempts to load a previous session state or initializes a new one based on user input.
/// Returns Ok(Some(messages)) if a session should start.
/// Returns Ok(None) if the user exits immediately during initial prompt.
/// Returns Err if a critical error occurs.
fn load_or_initialize_session(
    config: &RuntimeConfig,
) -> Result<Option<(Vec<ResponseMessage>, String)>> { // Returns messages AND initial goal
    let recovery_path = Path::new(RECOVERY_FILE_PATH);
    let mut messages_option: Option<Vec<ResponseMessage>> = None;
    let mut initial_goal: Option<String> = None;

    // --- Load State Logic ---
    if recovery_path.exists() {
        info!(
            "Found existing conversation state file: {}",
            RECOVERY_FILE_PATH
        );
        print!(
            "{}",
            "An incomplete session state was found. Resume? (Y/n): "
                .yellow()
                .bold()
        );
        io::stdout().flush()?;

        let mut user_choice = String::new();
        io::stdin().read_line(&mut user_choice)?;

        if user_choice.trim().to_lowercase() != "n" {
            match fs::read_to_string(recovery_path) {
                Ok(state_json) => match serde_json::from_str(&state_json) {
                    Ok(loaded_messages) => {
                        messages_option = Some(loaded_messages);
                        info!("Successfully resumed session from state file.");
                        println!("{}", "Resuming previous session...".cyan());
                        let _ = fs::remove_file(recovery_path);
                    }
                    Err(e) => {
                        error!("Failed to deserialize state file: {}. Starting fresh.", e);
                        println!(
                            "{}",
                            "Error reading state file. Starting a fresh session.".red()
                        );
                        let _ = fs::remove_file(recovery_path);
                    }
                },
                Err(e) => {
                    error!("Failed to read state file: {}. Starting fresh.", e);
                    println!(
                        "{}",
                        "Error reading state file. Starting a fresh session.".red()
                    );
                    let _ = fs::remove_file(recovery_path);
                }
            }
        } else {
            info!("User chose not to resume. Starting fresh.");
            println!("{}", "Starting a fresh session.".cyan());
            let _ = fs::remove_file(recovery_path);
        }
    }
    // --- End Load State Logic ---

    // --- Get Initial Query if messages_option is still None ---
    if messages_option.is_none() {
        println!("{}", "How can I help you?".cyan());
        print!("{} ", ">".green().bold());
        io::stdout().flush()?;

        let mut initial_input = String::new();
        io::stdin().read_line(&mut initial_input)?;
        let trimmed_input = initial_input.trim();

        if trimmed_input.is_empty() || trimmed_input.to_lowercase() == "exit" {
            return Ok(None); // User wants to exit immediately
        }
        initial_goal = Some(trimmed_input.to_string());
        // Initialize messages with system prompt only for Agent::run
        messages_option = Some(vec![ResponseMessage {
            role: "system".to_string(),
            content: Some(config.system_prompt.clone()),
            tool_calls: None,
            tool_call_id: None,
        }]);
    } else {
        // If resuming, we need to figure out the goal. For now, ask again.
        // TODO: Persist goal along with messages for better resume experience.
        println!("{}", "What is the main goal for this resumed session?".cyan());
        print!("{} ", ">".green().bold());
        io::stdout().flush()?;
        let mut goal_input = String::new();
        io::stdin().read_line(&mut goal_input)?;
        let trimmed_input = goal_input.trim();
        if trimmed_input.is_empty() {
             println!("{}", "Goal cannot be empty. Exiting.".red());
             return Ok(None);
        }
        initial_goal = Some(trimmed_input.to_string());
    }
    // --- End Initial Query ---

    // Ensure we have both messages and a goal if we got this far
    if let (Some(messages), Some(goal)) = (messages_option, initial_goal) {
        Ok(Some((messages, goal)))
    } else {
        // This case should ideally not be reached if logic above is correct
        error!("Failed to establish initial messages and goal.");
        Ok(None)
    }
}

/// Runs the main interactive conversation loop using the Agent.
async fn run_agent_session(
    config: &RuntimeConfig,
    client: &Client, // Keep client for potential direct use in tools?
    tool_provider: Arc<dyn ToolProvider>,
    initial_messages: Vec<ResponseMessage>, // Keep for potential state loading
    initial_goal: String,
) -> Result<()> {
    let agent = Agent::new(config.clone(), Arc::clone(&tool_provider))
        .context("Failed to create agent instance")?;

    // Agent::run manages the conversation loop internally
    info!("Starting agent run with goal: {}", initial_goal);

    // Use project_root from config as the working directory
    let working_dir = &config.project_root;

    // Call Agent::run
    match agent.run(&initial_goal, working_dir).await {
        Ok(agent_output) => {
            info!("Agent run finished successfully.");
            // --- Render Agent Output --- 
            println!("\n{}", "--- Agent Run Summary ---".bold());

            if let Some(summary) = agent_output.suggested_summary {
                println!("{}:", "Suggested Summary".cyan());
                println!("{}", summary);
            }

            if !agent_output.applied_tool_results.is_empty() {
                println!("\n{}:", "Tool Execution Results".cyan());
                for result in agent_output.applied_tool_results {
                    let status_color = match result.status {
                        volition_agent_core::ToolExecutionStatus::Success => "Success".green(),
                        volition_agent_core::ToolExecutionStatus::Failure => "Failure".red(),
                    };
                    println!(
                        "- Tool: {}, Status: {}",
                        result.tool_name.yellow(),
                        status_color
                    );
                    // Optionally print input/output (can be verbose)
                    // println!("  Input: {}", serde_json::to_string_pretty(&result.input).unwrap_or_default());
                    // println!("  Output: {}", result.output);
                }
            }

            if let Some(final_desc) = agent_output.final_state_description {
                 println!("\n{}:", "Final AI Message".cyan());
                 if let Err(e) = print_formatted(&final_desc) {
                    error!("Failed to render final AI message markdown: {}. Printing raw.", e);
                    println!("{}", final_desc);
                 } else {
                    println!(); // Add newline after formatted print
                 }
            }
            println!("-----------------------\n");
            // TODO: Add CLI-specific actions based on AgentOutput (e.g., commit changes)
        }
        Err(e) => {
            error!("Agent run failed: {:?}", e);
            println!("{}", "Agent run encountered an error:".red());
            println!("{:?}", e);
            // No state cleanup here as Agent doesn't manage state file directly
            return Err(e); // Propagate the error
        }
    }

    // Agent run finished, cleanup state file if it exists (though Agent doesn't create it)
    cleanup_session_state()
}


/// Cleans up the session state recovery file.
fn cleanup_session_state() -> Result<()> {
    let recovery_path = Path::new(RECOVERY_FILE_PATH);
    if recovery_path.exists() {
        if let Err(e) = fs::remove_file(recovery_path) {
            warn!("Failed to remove recovery state file on exit: {}", e);
        } else {
            info!("Removed recovery state file on clean exit.");
        }
    }
    Ok(())
}

// --- Main Application Entry Point ---

#[tokio::main]
async fn main() -> Result<()> {
    // Load .env file if present
    dotenvy::dotenv().ok();

    let cli = Cli::parse();

    // Setup tracing subscriber based on verbosity count
    let level = match cli.verbose {
        0 => Level::WARN,  // Default
        1 => Level::INFO,  // -v
        2 => Level::DEBUG, // -vv
        _ => Level::TRACE, // -vvv or more
    };

    let subscriber = FmtSubscriber::builder().with_max_level(level).finish();
    tracing::subscriber::set_global_default(subscriber).expect("setting default subscriber failed");

    // Load Configuration (using the function from the core library)
    let config = load_runtime_config()
        .context("Failed to load configuration from Volition.toml and environment")?;

    // Create HTTP Client
    let client = Client::builder()
        .timeout(Duration::from_secs(60))
        .build()
        .context("Failed to build HTTP client")?;

    // Instantiate the CLI's ToolProvider
    let tool_provider = Arc::new(CliToolProvider::new(client.clone())); // Pass client if needed by tools

    // --- Start Session --- 
    print_welcome_message();

    match load_or_initialize_session(&config)? {
        Some((initial_messages, initial_goal)) => {
            // Run the agent session
            if let Err(e) = run_agent_session(
                &config,
                &client,
                tool_provider,
                initial_messages, // Pass loaded/initialized messages
                initial_goal,
            )
            .await
            {
                // Error already printed in run_agent_session
                // Exit with non-zero status code
                std::process::exit(1);
            }
        }
        None => {
            // User exited immediately
            println!("\n{}", "Goodbye!".cyan());
        }
    }

    Ok(())
}
"}

