// --- Mock Tool Provider ---

#[derive(Clone)] // Clone needed for Arc<>
struct MockToolProvider {
    // Record calls made to execute_tool: (tool_name, input_args_json_string)
    // Use RefCell for interior mutability within Arc<&MockToolProvider>
    call_log: Arc<RefCell<Vec<(String, String)>>>,
    // Predefined outputs for specific tools
    outputs: HashMap<String, Result<String>>,
    // Definitions to provide
    definitions: Vec<ToolDefinition>,
}

impl MockToolProvider {
    fn new(definitions: Vec<ToolDefinition>, outputs: HashMap<String, Result<String>>) -> Self {
        Self {
            call_log: Arc::new(RefCell::new(Vec::new())),
            outputs,
            definitions,
        }
    }

    // Helper to create a simple definition for testing
    fn simple_def(name: &str) -> ToolDefinition {
        ToolDefinition {
            name: name.to_string(),
            description: format!("Mock tool {}", name),
            parameters: ToolParametersDefinition {
                param_type: "object".to_string(),
                properties: HashMap::new(), // No params for simplicity
                required: vec![],
            },
        }
    }
}

#[async_trait]
impl ToolProvider for MockToolProvider {
    fn get_tool_definitions(&self) -> Vec<ToolDefinition> {
        self.definitions.clone()
    }

    async fn execute_tool(&self, tool_name: &str, input: ToolInput, _working_dir: &Path) -> Result<String> {
        // Log the call
        let input_json = serde_json::to_string(&input.arguments).unwrap_or_default();
        self.call_log.borrow_mut().push((tool_name.to_string(), input_json));

        // Return predefined output or error
        match self.outputs.get(tool_name) {
            Some(Ok(output)) => Ok(output.clone()),
            Some(Err(e)) => Err(anyhow::anyhow!("{}", e)), // Clone error message
            None => Err(anyhow!("MockToolProvider: No output defined for tool '{}'", tool_name)),
        }
    }
}

// --- Test Helpers ---

fn create_test_config() -> RuntimeConfig {
    // Create a basic RuntimeConfig for tests
    let mut models = HashMap::new();
    models.insert("test-model-key".to_string(), ModelConfig {
        model_name: "test-model".to_string(),
        endpoint: "http://localhost:1234/test".to_string(),
        parameters: toml::Value::Table(Default::default()),
    });
    RuntimeConfig {
        system_prompt: "Test System Prompt".to_string(),
        selected_model: "test-model-key".to_string(),
        models,
        api_key: "test-api-key".to_string(),
        project_root: PathBuf::from("."),
    }
}

// --- Basic Agent Tests ---

// TODO: Currently difficult to test Agent::run end-to-end without mocking
//       the HTTP client or refactoring api::get_chat_completion for injection.
//       These tests focus on parts of the logic or require careful setup.

#[tokio::test]
async fn test_agent_initialization() {
    let config = create_test_config();
    let mock_provider = Arc::new(MockToolProvider::new(vec![], HashMap::new()));
    let agent_result = Agent::new(config, mock_provider);
    assert!(agent_result.is_ok());
}

