    // 4. Define Mock API Responses
    let goal = "What is the weather?";
    let tool_call_id = "call_123";
    let tool_args = json!({ "arg": "today" }); // Example args

    // Mock 1: Initial call -> Respond with tool call request
    let mock_response_1 = json!({
        "id": "resp1",
        "choices": [{
            "index": 0,
            "message": {
                "role": "assistant",
                "content": null,
                "tool_calls": [{
                    "id": tool_call_id,
                    "type": "function",
                    "function": {
                        "name": tool_name,
                        "arguments": tool_args.to_string() // Arguments as JSON string
                    }
                }]
            },
