    let api_mock_1 = server.mock_async(|when, then| {
        when.method(POST).path("/"); // Assuming endpoint is just base URL path
        then.status(200).json_body(mock_response_1);
    }).await;

    // Mock 2: Call after tool result -> Respond with final answer
    let final_answer = "The weather today is sunny.";
    let mock_response_2 = json!({
        "id": "resp2",
        "choices": [{
            "index": 0,
            "message": {
                "role": "assistant",
                "content": final_answer,
                "tool_calls": null
            },
            "finish_reason": "stop"
        }]
    });
     let api_mock_2 = server.mock_async(|when, then| {
        when.method(POST).path("/");
        then.status(200).json_body(mock_response_2);
    }).await;

    // 5. Run Agent
    let working_dir = PathBuf::from(".");
    let agent_output = agent.run(goal, &working_dir).await?;

    // 6. Assertions
    api_mock_1.assert_async().await; // Check first API call happened
    api_mock_2.assert_async().await; // Check second API call happened
    assert_eq!(api_mock_1.hits(), 1, "Mock 1 should be hit once");
    assert_eq!(api_mock_2.hits(), 1, "Mock 2 should be hit once");

    // Check tool provider log
    let calls = mock_provider.call_log.borrow();
    assert_eq!(calls.len(), 1, "Tool provider should have been called once");
    assert_eq!(calls[0].0, tool_name, "Tool name mismatch");
    assert_eq!(calls[0].1, tool_args.to_string(), "Tool args mismatch");

    // Check agent output
    assert!(agent_output.suggested_summary.is_some()); // Placeholder check
    assert_eq!(agent_output.applied_tool_results.len(), 1, "Should have one tool result");
    let tool_result = &agent_output.applied_tool_results[0];
    assert_eq!(tool_result.tool_call_id, tool_call_id);
    assert_eq!(tool_result.tool_name, tool_name);
    assert_eq!(tool_result.input, tool_args);
    assert_eq!(tool_result.output, tool_output_content);
    assert_eq!(tool_result.status, ToolExecutionStatus::Success);
    assert_eq!(agent_output.final_state_description, Some(final_answer.to_string()));

    Ok(())
}

