// Allow unused imports during refactoring
use super::shell::execute_shell_command;
use anyhow::{Result};
use std::path::Path;
use tracing::{debug, info};

// Function signature for shell execution dependency
type ShellExecutor = fn(&str, &Path) -> std::pin::Pin<Box<dyn std::future::Future<Output = Result<String>> + Send>>;

#[cfg(not(test))]
fn check_ripgrep_installed() -> Result<()> {
    use std::process::Command;
    // ... (check implementation unchanged)
    let command_name = "rg";
    let check_command = if cfg!(target_os = "windows") {
        format!("Get-Command {}", command_name)
    } else {
        format!("command -v {}", command_name)
    };
    let output = Command::new(if cfg!(target_os = "windows") { "powershell" } else { "sh" })
        .arg(if cfg!(target_os = "windows") { "-Command" } else { "-c" })
        .arg(&check_command)
        .output()?;
    if output.status.success() {
        Ok(())
    } else {
        Err(anyhow::anyhow!(
            "\'ripgrep\' (rg) command not found. Please install it and ensure it\'s in your PATH. It\'s required for search/definition tools.\nInstallation instructions: https://github.com/BurntSushi/ripgrep#installation"
        ))
    }
}

#[cfg(test)]
fn check_ripgrep_installed() -> Result<()> {
    Ok(())
}

// Helper function to box the future for the function pointer type
fn box_shell_exec(cmd: &str, wd: &Path) -> std::pin::Pin<Box<dyn std::future::Future<Output = Result<String>> + Send>> {
    Box::pin(execute_shell_command(cmd, wd))
}

/// Searches for a text pattern using ripgrep, using dependency injection for shell execution.
pub async fn search_text_impl(
    pattern: &str,
    search_path: Option<&str>,
    file_glob: Option<&str>,
    case_sensitive: Option<bool>,
    context_lines: Option<u32>,
    max_results: Option<usize>,
    working_dir: &Path,
    shell_exec: ShellExecutor, // Pass the shell executor function
) -> Result<String> {
    check_ripgrep_installed()?;

    let path_arg = search_path.unwrap_or(".");
    let glob_arg = file_glob.unwrap_or("*");
    let ignore_case_flag = !case_sensitive.unwrap_or(false);
    let context_arg = context_lines.unwrap_or(1);
    let max_lines = max_results.unwrap_or(50);

    info!(
        "Searching for pattern: '{}' in path: '{}' (glob: '{}', context: {}, ignore_case: {}) -> max {} lines",
        pattern, path_arg, glob_arg, context_arg, ignore_case_flag, max_lines
    );

    let context_str = context_arg.to_string();
    let mut rg_cmd_vec = vec![
        "rg", "--pretty", "--trim", "--context", &context_str, "--glob", glob_arg,
    ];
    if ignore_case_flag { rg_cmd_vec.push("--ignore-case"); }
    rg_cmd_vec.push(pattern);
    rg_cmd_vec.push(path_arg);

    let rg_cmd_base = rg_cmd_vec.iter().map(|s| format!("'{}'", s.replace('\'', "'\\''"))).collect::<Vec<_>>().join(" ");
    let full_cmd = format!("{} | head -n {}", rg_cmd_base, max_lines);

    debug!("Executing search command: {}", full_cmd);

    // Use the injected shell executor
    let result = shell_exec(&full_cmd, working_dir).await?;

    let no_stdout = result.contains("\nStdout:\n<no output>");
    if no_stdout {
        Ok(format!("No matches found for pattern: '{}' in path: '{}' matching glob: '{}'", pattern, path_arg, glob_arg))
    } else {
        Ok(result)
    }
}

// Public wrapper using the real shell executor
pub async fn search_text(
    pattern: &str,
    search_path: Option<&str>,
    file_glob: Option<&str>,
    case_sensitive: Option<bool>,
    context_lines: Option<u32>,
    max_results: Option<usize>,
    working_dir: &Path,
) -> Result<String> {
    search_text_impl(pattern, search_path, file_glob, case_sensitive, context_lines, max_results, working_dir, box_shell_exec).await
}

/// Finds potential Rust definition sites using ripgrep, using dependency injection.
pub async fn find_rust_definition_impl(
    symbol: &str,
    search_path: Option<&str>,
    working_dir: &Path,
    shell_exec: ShellExecutor, // Pass the shell executor function
) -> Result<String> {
    check_ripgrep_installed()?;

    let directory_arg = search_path.unwrap_or(".");
    info!("Finding Rust definition for symbol: {} in directory: {}", symbol, directory_arg);

    let file_pattern = "*.rs";
    let escaped_symbol = regex::escape(symbol);
    let pattern = format!(r"^(?:pub\s+)?(?:unsafe\s+)?(?:async\s+)?(fn|struct|enum|trait|const|static|type|mod|impl|macro_rules!)\s+{}\\b", escaped_symbol);

    let rg_cmd_vec = vec![
        "rg", "--pretty", "--trim", "--glob", file_pattern, "--ignore-case", "--max-count=10", "-e", &pattern, directory_arg,
    ];
    let full_cmd = rg_cmd_vec.iter().map(|s| format!("'{}'", s.replace('\'', "'\\''"))).collect::<Vec<_>>().join(" ");

    debug!("Executing find rust definition command: {}", full_cmd);

    // Use the injected shell executor
    let result = shell_exec(&full_cmd, working_dir).await?;

    let no_stdout = result.contains("\nStdout:\n<no output>");
    if no_stdout {
        Ok(format!("No Rust definition found for symbol: {}", symbol))
    } else {
        Ok(result)
    }
}

// Public wrapper using the real shell executor
pub async fn find_rust_definition(
    symbol: &str,
    search_path: Option<&str>,
    working_dir: &Path,
) -> Result<String> {
    find_rust_definition_impl(symbol, search_path, working_dir, box_shell_exec).await
}

