    }

    fn test_working_dir() -> PathBuf {
        tempdir().map(|d| d.into_path()).unwrap_or_default()
    }

    #[tokio::test]
    async fn test_check_ripgrep_installed_mock() {
        assert!(check_ripgrep_installed().is_ok());
    }

    #[tokio::test]
    async fn test_search_text_no_matches() {
        let pattern = "no_match_pattern";
        let working_dir = test_working_dir();

        // Create mock context
        let ctx = Mockexecute_shell_command::ctx();
        // Set expectation: any command containing pattern returns no output
        ctx.expect()
            .with(predicate::str::contains(pattern), predicate::always())
            .times(1)
            .returning(|_,_| Ok("Command executed: ...\nStatus: 1\nStdout:\n<no output>\nStderr:\n<no output>".to_string()));

        let result = search_text(pattern, None, None, None, None, None, &working_dir).await;
        assert!(result.is_ok());
        assert!(result.unwrap().contains("No matches found"));
    }

     #[tokio::test]
    async fn test_find_rust_definition_found() {
        let symbol = "find_this_symbol";
        let working_dir = test_working_dir();
        let expected_stdout = "src/lib.rs:10:1:pub fn find_this_symbol() {}";

        // Create mock context
        let ctx = Mockexecute_shell_command::ctx();
        // Set expectation: command containing symbol returns specific output
        ctx.expect()
            .with(predicate::str::contains(symbol), predicate::always())
            .times(1)
            .returning(move |_,_| Ok(format!("Command executed: ...\nStatus: 0\nStdout:\n{}\nStderr:\n<no output>", expected_stdout)));

        let result = find_rust_definition(symbol, None, &working_dir).await;
        assert!(result.is_ok());
        let output = result.unwrap();
        assert!(output.contains(expected_stdout));
        assert!(!output.contains("No Rust definition found"));
    }
}
