        fs::write(&test_file_path, file_content)?;

        let result = find_rust_definition(symbol, None, &working_dir).await;
        assert!(result.is_ok(), "find_rust_definition failed: {:?}", result.err());
        let output = result.unwrap();
        println!("find_rust_definition output:\n{}", output);
        // Check that the output contains the actual line with the definition
        assert!(output.contains("pub fn find_this_test_fn()"));
        // Check that it includes the Stdout section from the shell command output
        assert!(output.contains("\nStdout:\n"));
        // Check that it *doesn't* say "No definition found"
        assert!(!output.contains("No Rust definition found"));
        Ok(())
    }
}
