/// Finds potential Rust definition sites using ripgrep.
pub async fn find_rust_definition(
    symbol: &str,
    search_path: Option<&str>,
    working_dir: &Path,
) -> Result<String> {
    check_ripgrep_installed()?;

    let directory_or_file_arg = search_path.unwrap_or(".");
    let is_likely_dir = !directory_or_file_arg.contains('.') || directory_or_file_arg == ".";

    info!("Finding Rust definition for symbol: {} in path: {}", symbol, directory_or_file_arg);

    let file_pattern = "*.rs";
    let escaped_symbol = regex::escape(symbol);
    let pattern = format!(
        r"(?:pub\s+)?(?:unsafe\s+)?(?:async\s+)?(fn|struct|enum|trait|const|static|type|mod|impl|macro_rules!)\s+{}\\b",
        escaped_symbol
    );

    // Build command arguments directly for std::process::Command
    let mut command = Command::new("rg");
    command.current_dir(working_dir);
    command.arg("--trim"); // Keep trim for cleaner output
    if is_likely_dir {
        command.arg("--glob").arg(file_pattern);
    }
    command.arg("--ignore-case");
    command.arg("--max-count=10");
    command.arg("-e");
    command.arg(&pattern);
    command.arg(directory_or_file_arg);

    command.stdout(Stdio::piped());
    command.stderr(Stdio::piped());

    debug!("Executing find rust definition command: {:?}", command);

    // Execute directly
    let output = command.output()
        .map_err(|e| anyhow::anyhow!("Failed to execute rg command: {}", e))?;

    let stdout = String::from_utf8_lossy(&output.stdout).to_string();
    let stderr = String::from_utf8_lossy(&output.stderr).to_string();
    let status = output.status.code().unwrap_or(-1);

    // Format output similar to execute_shell_command for consistency?
    // Or return raw stdout if found?
    // Let's return raw stdout on success for now, and specific message on failure.

    if !output.status.success() || stdout.is_empty() {
         debug!("rg command failed or produced no output. Status: {}, Stderr: {}", status, stderr);
         Ok(format!("No Rust definition found for symbol: {}", symbol))
    } else {
         // Return just the stdout content, which should be the matching lines
         Ok(stdout.trim().to_string())
    }
}

