            ..Default::default()
        }];
        let model_config = create_test_model_config(&full_endpoint_url, None);
        let runtime_config = create_test_runtime_config(model_key, model_config.clone());
        let tool_definitions = create_mock_tool_definitions();

        let mock = server
            .mock_async(|when, then| {
                when.method(POST).path(endpoint_path);
                then.status(500).body("Server error");
            })
            .await;

        let client = Client::new();
        let result =
            get_chat_completion(&client, &runtime_config, messages, &tool_definitions).await;
        assert_eq!(mock.hits(), TEST_MAX_RETRIES as usize + 1);
        assert!(result.is_err(), "Expected Err, got Ok");
        assert!(result.err().unwrap().to_string().contains("API error: 500"));
    }
}
"}

{"path":"volition-agent-core/src/tools/search.rs","content":"// volition-agent-core/src/tools/search.rs

use super::shell::execute_shell_command;
use anyhow::{Result};
use std::path::Path;
use tracing::{debug, info};

#[cfg(not(test))]
fn check_ripgrep_installed() -> Result<()> {
    use std::process::Command;
    let command_name = "rg";
    let check_command = if cfg!(target_os = "windows") {
        format!("Get-Command {}", command_name)
    } else {
        format!("command -v {}", command_name)
    };
    let output = Command::new(if cfg!(target_os = "windows") { "powershell" } else { "sh" })
        .arg(if cfg!(target_os = "windows") { "-Command" } else { "-c" })
        .arg(&check_command)
        .output()?;
    if output.status.success() {
        Ok(())
    } else {
        Err(anyhow::anyhow!(
            "\'ripgrep\' (rg) command not found. Please install it and ensure it\'s in your PATH. It\'s required for search/definition tools.\nInstallation instructions: https://github.com/BurntSushi/ripgrep#installation"
        ))
    }
}

#[cfg(test)]
fn check_ripgrep_installed() -> Result<()> {
    Ok(())
}

/// Searches for a text pattern using ripgrep.
pub async fn search_text(
    pattern: &str,
    search_path: Option<&str>,
    file_glob: Option<&str>,
    case_sensitive: Option<bool>,
    context_lines: Option<u32>,
    max_results: Option<usize>,
    working_dir: &Path,
) -> Result<String> {
    check_ripgrep_installed()?;

    let path_arg = search_path.unwrap_or(".");
    let glob_arg = file_glob.unwrap_or("*");
    let ignore_case_flag = !case_sensitive.unwrap_or(false);
    let context_arg = context_lines.unwrap_or(1);
    let max_lines = max_results.unwrap_or(50);

    info!(
        "Searching for pattern: '{}' in path: '{}' (glob: '{}', context: {}, ignore_case: {}) -> max {} lines",
        pattern, path_arg, glob_arg, context_arg, ignore_case_flag, max_lines
    );

    let context_str = context_arg.to_string();
    let mut rg_cmd_vec = vec![
        "rg", "--pretty", "--trim", "--context", &context_str, "--glob", glob_arg,
    ];
    if ignore_case_flag { rg_cmd_vec.push("--ignore-case"); }
    rg_cmd_vec.push(pattern);
    rg_cmd_vec.push(path_arg);

    let mut rg_cmd_parts = Vec::new();
    for arg in rg_cmd_vec.iter() {
        if *arg == pattern || *arg == path_arg {
             rg_cmd_parts.push(arg.to_string());
        } else {
            rg_cmd_parts.push(format!("'{}'", arg.replace('\'', "'\\''")));
        }
    }
    let rg_cmd_base = rg_cmd_parts.join(" ");

    let full_cmd = format!("{} | head -n {}", rg_cmd_base, max_lines);

    debug!("Executing search command via shell: {}", full_cmd);

    let result = execute_shell_command(&full_cmd, working_dir).await?;

    let no_stdout = result.contains("\nStdout:\n<no output>");
    let no_match_status = result.contains("\nStatus: 1\n"); 

    if no_stdout || no_match_status {
        Ok(format!(
            "No matches found for pattern: '{}' in path: '{}' matching glob: '{}'",
            pattern, path_arg, glob_arg
        ))
    } else {
        Ok(result)
    }
}

/// Finds potential Rust definition sites using ripgrep.
pub async fn find_rust_definition(
    symbol: &str,
    search_path: Option<&str>,
    working_dir: &Path,
) -> Result<String> {
    check_ripgrep_installed()?;

    let directory_or_file_arg = search_path.unwrap_or(".");
    let is_dir = working_dir.join(directory_or_file_arg).is_dir();

    info!(
        "Finding Rust definition for symbol: {} in path: {} (is_dir: {})",
        symbol, directory_or_file_arg, is_dir
    );

    let file_pattern = "*.rs";
    let escaped_symbol = regex::escape(symbol);
    let pattern = format!(
        r"(?:pub\s+)?(?:unsafe\s+)?(?:async\s+)?(fn|struct|enum|trait|const|static|type|mod|impl|macro_rules!)\s+{}\\b",
        escaped_symbol
    );

    let mut command_parts = vec!["rg".to_string()];
    command_parts.push("--trim".to_string());
    if is_dir {
        command_parts.push("--glob".to_string());
        command_parts.push(file_pattern.to_string()); 
    }
    command_parts.push("--ignore-case".to_string());
    command_parts.push("--max-count=10".to_string());
    command_parts.push("-e".to_string());
    command_parts.push(format!("'{}'", pattern.replace('\'', "'\\''"))); 
    command_parts.push(directory_or_file_arg.to_string());

    let full_cmd = command_parts.join(" ");

    debug!("Executing find rust definition command via shell: {}", full_cmd);

    let result = execute_shell_command(&full_cmd, working_dir).await?;

    let no_stdout = result.contains("\nStdout:\n<no output>");
    let no_match_status = result.contains("\nStatus: 1\n");

    if no_stdout || no_match_status {
        Ok(format!("No Rust definition found for symbol: {}", symbol))
    } else {
        Ok(result)
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use std::path::PathBuf;
    use tempfile::tempdir;
    use tokio;
    use std::fs;

    fn test_working_dir() -> PathBuf {
        tempdir().expect("Failed to create temp dir").into_path()
    }

    #[tokio::test]
    async fn test_check_ripgrep_installed_mock() {
        assert!(check_ripgrep_installed().is_ok());
    }

    #[tokio::test]
    async fn test_search_text_no_matches() {
        let pattern = "pattern_that_will_not_match_in_a_million_years";
        let working_dir = test_working_dir();
        fs::write(working_dir.join("dummy.txt"), "content").unwrap(); 
        let result = search_text(pattern, None, None, None, None, None, &working_dir).await;
        assert!(result.is_ok());
        let output = result.unwrap();
        println!("search_text_no_matches output:\n{}", output);
        assert!(output.contains("No matches found"), "Output should indicate no matches were found");
        assert!(!output.contains("\nStatus: 1\n")); 
    }

     #[tokio::test]
     // Ignore this test as it relies on rg being installed and specific shell/rg behavior
     #[ignore = "Relies on external rg command and shell execution details"]
    async fn test_find_rust_definition_found_in_test_file() -> Result<()> {
        let symbol = "find_this_test_fn_abc"; 
        let working_dir = test_working_dir();
        let test_file_name = "test_src_find_def.rs";
        let test_file_path = working_dir.join(test_file_name);
        let file_content = format!("\n  // Some comment\npub fn {}() {{\n    println!(\"Found!\");\n}}\n", symbol);
        fs::write(&test_file_path, file_content)?;

        let result = find_rust_definition(symbol, None, &working_dir).await;

        assert!(result.is_ok(), "find_rust_definition failed: {:?}", result.err());
        let output = result.unwrap();
        println!("find_rust_definition output:\n{}", output);

        let expected_line = format!("pub fn {}()", symbol);
        assert!(output.contains(&expected_line), "Output did not contain function signature");
        assert!(output.contains("\nStdout:\n"), "Output did not contain Stdout section");
        assert!(!output.contains("No Rust definition found"), "Output incorrectly stated no definition found");
        Ok(())
    }
}
"}

{"content":"// volition-agent-core/src/config.rs
use anyhow::{anyhow, Context, Result};
use serde::Deserialize;
use std::collections::HashMap;
// Removed env, fs, Path - No longer needed for core parsing/validation
use url::Url;

// --- Configuration Structures ---

/// Represents the validated configuration needed by the Agent.
/// Does not include environment-specific details like project_root.
#[derive(Deserialize, Debug, Clone)] // Removed project_root
pub struct RuntimeConfig {
    pub system_prompt: String,
    pub selected_model: String,
    pub models: HashMap<String, ModelConfig>,
    #[serde(skip)] // Api key is not deserialized from TOML
    pub api_key: String,
    // Removed project_root: PathBuf
}

/// Represents the configuration for a specific AI model.
#[derive(Deserialize, Debug, Clone)]
pub struct ModelConfig {
    pub model_name: String,
    pub parameters: toml::Value,
    pub endpoint: String,
}

impl RuntimeConfig {
    /// Returns a reference to the currently selected ModelConfig.
    pub fn selected_model_config(&self) -> Result<&ModelConfig> {
        self.models.get(&self.selected_model).ok_or_else(|| {
            anyhow!(
                "Internal inconsistency: Selected model key '{}' not found in models map.",
                self.selected_model
            )
        })
    }
}

/// Parses TOML configuration content and validates it against the provided API key.
/// Renamed from load_runtime_config.
pub fn parse_and_validate_config(
    config_toml_content: &str,
    api_key: String,
) -> Result<RuntimeConfig> {
    // --- Validate API Key ---
    if api_key.is_empty() {
        return Err(anyhow!("Provided API key is empty."));
    }

    // --- Deserialize Configuration File Content ---
    // Use a specific error context for parsing
    let partial_config: RuntimeConfigPartial = toml::from_str(config_toml_content)
        .context("Failed to parse configuration TOML content. Check TOML syntax.")?;

    // --- Construct Full RuntimeConfig (without project_root) ---
    let config = RuntimeConfig {
        system_prompt: partial_config.system_prompt,
        selected_model: partial_config.selected_model,
        models: partial_config.models,
        api_key,
        // project_root is no longer part of RuntimeConfig
    };

    // --- Validation ---
    // Use generic context for validation errors
    if config.system_prompt.trim().is_empty() {
        return Err(anyhow!("'system_prompt' in config content is empty."));
    }
    if config.selected_model.trim().is_empty() {
        return Err(anyhow!("Top-level 'selected_model' key in config content is empty."));
    }
    if config.models.is_empty() {
        return Err(anyhow!("The [models] section in config content is empty."));
    }

    // Check if selected model exists using the method
    config.selected_model_config().context("Validation failed for selected model in config content")?;

    for (key, model) in &config.models {
        if model.model_name.trim().is_empty() {
            return Err(anyhow!("Model definition '{}' has an empty 'model_name'.", key));
        }
        if model.endpoint.trim().is_empty() {
            return Err(anyhow!("Model definition '{}' has an empty 'endpoint'.", key));
        }
        Url::parse(&model.endpoint).with_context(|| {
            format!(
                "Invalid URL format for endpoint ('{}') in model definition '{}'.",
                model.endpoint,
                key
            )
        })?;
        // Basic check for parameters structure if needed (e.g., ensure it's a table)
        if !model.parameters.is_table() && !model.parameters.is_string() && model.parameters.as_str() != Some("{}") {
            // Allow empty table represented as "{}" string or actual table
             return Err(anyhow!(
                "Model definition '{}' has invalid 'parameters'. Expected a TOML table.",
                key
            ));
        }
    }

    // No longer log path here, as it's not known
    tracing::info!("Successfully parsed and validated configuration content.");
    Ok(config)
}

// Helper struct for deserializing only the parts from the TOML file
#[derive(Deserialize)]
struct RuntimeConfigPartial {
    system_prompt: String,
    selected_model: String,
    models: HashMap<String, ModelConfig>,
}

#[cfg(test)]
mod tests {
    use super::*;
    // Removed env, fs, PathBuf, tempdir - no longer needed
    use toml;

    // Helper function creates valid TOML content string
    fn valid_config_content() -> String {
        r#"
            system_prompt = "You are a helpful assistant."
            selected_model = "gpt4"
            [models.gpt4]
            model_name = "gpt-4-turbo"
            endpoint = "https://api.openai.com/v1"
            parameters = { temperature = 0.7 }
            [models.ollama_llama3]
            model_name = "llama3"
            endpoint = "http://localhost:11434/api"
            parameters = { top_p = 0.9 }
        "#.to_string()
    }

    // --- Tests for parse_and_validate_config ---

    // Test the successful parsing scenario
    #[test]
    fn test_parse_validate_success() {
        let content = valid_config_content();
        let api_key = "test_api_key_123".to_string();

        let result = parse_and_validate_config(&content, api_key.clone());

        assert!(result.is_ok(), "Parse/validate failed: {:?}", result.err());
        let config = result.unwrap();
        assert_eq!(config.api_key, api_key);
        assert_eq!(config.system_prompt, "You are a helpful assistant.");
        assert_eq!(config.selected_model, "gpt4");
        assert!(config.models.contains_key("gpt4"));
        assert!(config.models.contains_key("ollama_llama3"));
        assert_eq!(config.models["gpt4"].model_name, "gpt-4-turbo");
        assert_eq!(config.models["gpt4"].endpoint, "https://api.openai.com/v1");
        assert!(config.models["gpt4"].parameters.is_table());
        // Check selected model config retrieval
        let selected_conf = config.selected_model_config().unwrap();
        assert_eq!(selected_conf.model_name, "gpt-4-turbo");
    }

    // Test when API key is empty
    #[test]
    fn test_parse_validate_empty_api_key() {
        let content = valid_config_content();
        let api_key = "".to_string(); // Empty API key

        let result = parse_and_validate_config(&content, api_key);

        assert!(result.is_err());
        let error_message = result.err().unwrap().to_string();
        assert!(error_message.contains("Provided API key is empty"));
    }

    // Test invalid TOML syntax
    #[test]
    fn test_parse_validate_invalid_toml() {
        let content = r#"
            system_prompt = "Valid prompt"
            selected_model = "gpt4 # Missing closing quote
            [models.gpt4]
            # ...
        "#;
        let api_key = "dummy_key".to_string();

        let result = parse_and_validate_config(content, api_key);

        assert!(result.is_err());
        let error_message = result.err().unwrap().to_string();
        assert!(error_message.contains("Failed to parse configuration TOML content"));
    }

    // Test validation: selected_model key doesn't exist
    #[test]
    fn test_parse_validate_missing_selected_key() {
        let content = r#"
            system_prompt = "Valid prompt"
            selected_model = "nonexistent"
            [models.gpt4]
            model_name = "gpt4-real"
            endpoint = "https://example.com"
            parameters = {}
        "#;
        let api_key = "dummy_key".to_string();

        let result = parse_and_validate_config(content, api_key);

        assert!(result.is_err());
        let error_message = result.err().unwrap().to_string();
        assert!(error_message.contains("Selected model key 'nonexistent' not found"));
    }

    // Test validation: empty system_prompt
    #[test]
    fn test_parse_validate_empty_system_prompt() {
        let content = r#"
            system_prompt = "    " # Empty after trim
            selected_model = "gpt4"
            [models.gpt4]
            model_name = "gpt4-real"
            endpoint = "https://example.com"
            parameters = {}
        "#;
        let api_key = "dummy_key".to_string();

        let result = parse_and_validate_config(content, api_key);
        assert!(result.is_err());
        assert!(result.err().unwrap().to_string().contains("'system_prompt' in config content is empty"));
    }

    // Test validation: invalid endpoint URL
    #[test]
    fn test_parse_validate_invalid_endpoint() {
        let content = r#"
            system_prompt = "Valid"
            selected_model = "gpt4"
            [models.gpt4]
            model_name = "gpt4-real"
            endpoint = "invalid url"
            parameters = {}
        "#;
        let api_key = "dummy_key".to_string();

        let result = parse_and_validate_config(content, api_key);
        assert!(result.is_err());
        assert!(result.err().unwrap().to_string().contains("Invalid URL format for endpoint"));
    }
}
