fn is_git_command_denied(command_name: &str, args: &[String]) -> bool {
    // ... (deny list unchanged)
    let denied_commands: HashSet<&str> = [
        "push", "reset", "rebase", "checkout", "merge", "clone", "remote", "fetch", "pull",
    ]
    .iter()
    .cloned()
    .collect();
    if denied_commands.contains(command_name) { return true; }
    if command_name == "branch" && args.contains(&"-D".to_string()) { return true; }
    false
}

