#![cfg(test)]

use super::*;
use std::sync::{Arc, Mutex};
use std::collections::HashMap;
use std::path::PathBuf;

use anyhow::Result;
use httpmock::prelude::*;
use serde_json::json;
use tracing_subscriber;

// --- Mock Tool Provider ---

#[derive(Clone)]
struct MockToolProvider {
    call_log: Arc<Mutex<Vec<(String, String)>>>,
    outputs: HashMap<String, Result<String, String>>,
    definitions: Vec<ToolDefinition>,
}

impl MockToolProvider {
    fn new(definitions: Vec<ToolDefinition>, outputs: HashMap<String, Result<String, String>>) -> Self {
        Self {
            call_log: Arc::new(Mutex::new(Vec::new())),
            outputs,
            definitions,
        }
    }

    fn simple_def(name: &str) -> ToolDefinition {
        ToolDefinition {
            name: name.to_string(),
            description: format!("Mock tool {}", name),
            parameters: ToolParametersDefinition {
                param_type: "object".to_string(),
                properties: HashMap::from([(
                    "arg".to_string(),
                    ToolParameter {
                        param_type: ToolParameterType::String,
                        description: "An argument".to_string(),
                        enum_values: None,
                        items: None,
                    },
                )]),
                required: vec![],
            },
        }
    }
}

#[async_trait]
impl ToolProvider for MockToolProvider {
    fn get_tool_definitions(&self) -> Vec<ToolDefinition> {
        self.definitions.clone()
    }

    async fn execute_tool(&self, tool_name: &str, input: ToolInput, _working_dir: &Path) -> Result<String> {
        let input_json = serde_json::to_string(&input.arguments).unwrap_or_default();
        self.call_log.lock().unwrap().push((tool_name.to_string(), input_json));

        match self.outputs.get(tool_name) {
            Some(Ok(output)) => Ok(output.clone()),
            Some(Err(e)) => Err(anyhow!("{}", e.clone())),
            None => Err(anyhow!("MockToolProvider: No output defined for tool '{}'", tool_name)),
        }
    }
}

// --- Test Helpers ---

const TEST_ENDPOINT_PATH: &str = "/test/completions";

fn create_test_config(mock_server_base_url: &str) -> RuntimeConfig {
    let mock_endpoint = format!("{}{}", mock_server_base_url, TEST_ENDPOINT_PATH);
    let mut models = HashMap::new();
    models.insert(
        "test-model-key".to_string(),
        ModelConfig {
            model_name: "test-model".to_string(),
            endpoint: mock_endpoint,
            parameters: toml::Value::Table(Default::default()),
        },
    );
    RuntimeConfig {
        system_prompt: "Test System Prompt".to_string(),
        selected_model: "test-model-key".to_string(),
        models,
        api_key: "test-api-key".to_string(),
    }
}

// --- Agent Tests ---

#[tokio::test]
async fn test_agent_initialization() {
    let config = create_test_config("http://unused");
    let mock_provider = Arc::new(MockToolProvider::new(vec![], HashMap::new()));
    let agent_result = Agent::new(config, mock_provider);
    assert!(agent_result.is_ok());
}

#[tokio::test]
async fn test_agent_run_single_tool_call_success() -> Result<()> {
    let _ = tracing_subscriber::fmt().with_test_writer().try_init();

    let server = MockServer::start_async().await;
    let mock_base_url = server.base_url();

    let tool_name = "get_weather";
    let tool_defs = vec![MockToolProvider::simple_def(tool_name)];
    let mut tool_outputs = HashMap::new();
    let tool_output_content = "The weather is sunny.".to_string();
    tool_outputs.insert(tool_name.to_string(), Ok(tool_output_content.clone()));
    let mock_provider = Arc::new(MockToolProvider::new(tool_defs.clone(), tool_outputs));

    let config = create_test_config(&mock_base_url);
    let agent = Agent::new(config.clone(), mock_provider.clone())?;

    let goal = "What is the weather?";
    let tool_call_id = "call_123";
    let tool_args = json!({ "arg": "today" });

    // --- Mock 1 Setup (Initial Request) ---
    let expected_messages_1 = json!([
        { "role": "system", "content": config.system_prompt },
        { "role": "user", "content": goal },
    ]);
    let expected_body_1 = json!({
        "model": config.selected_model_config().unwrap().model_name,
        "messages": expected_messages_1,
        "tools": [
            { "type": "function", "function": tool_defs[0] }
        ]
    });
    let mock_response_1 = json!({
        "id": "resp1",
        "choices": [{
            "index": 0,
            "message": {
                "role": "assistant",
                "content": null,
                "tool_calls": [{
                    "id": tool_call_id,
                    "type": "function",
                    "function": {
                        "name": tool_name,
                        "arguments": tool_args.to_string()
                    }
                }]
            },
            "finish_reason": "tool_calls"
        }]
    });
    let api_mock_1 = server
        .mock_async(|when, then| {
            when.method(POST)
                .path(TEST_ENDPOINT_PATH)
                .json_body(expected_body_1.clone());
            then.status(200).json_body(mock_response_1);
        })
        .await;

    // --- Mock 2 Setup (Request with Tool Result) ---
    let final_answer = "The weather today is sunny.";
    let mock_response_2 = json!({
        "id": "resp2",
        "choices": [{
            "index": 0,
            "message": {
                "role": "assistant",
                "content": final_answer,
                "tool_calls": null
            },
            "finish_reason": "stop"
        }]
    });
    let expected_messages_2 = json!([
        { "role": "system", "content": config.system_prompt },
        { "role": "user", "content": goal },
        {
            "role": "assistant",
            "tool_calls": [{
                "id": tool_call_id,
                "type": "function",
                "function": {
                    "name": tool_name,
                    "arguments": tool_args.to_string()
                }
            }]
        },
        {
            "role": "tool",
            "content": tool_output_content,
            "tool_call_id": tool_call_id
        }
    ]);
    let expected_body_2 = json!({
        "model": config.selected_model_config().unwrap().model_name,
        "messages": expected_messages_2,
        "tools": [
            { "type": "function", "function": tool_defs[0] }
        ]
    });
    let api_mock_2 = server
        .mock_async(|when, then| {
            when.method(POST)
                .path(TEST_ENDPOINT_PATH)
                .json_body(expected_body_2.clone());
            then.status(200).json_body(mock_response_2);
        })
        .await;

    // --- Run Agent ---
    let working_dir = PathBuf::from(".");
    println!("Running agent...");
    let agent_output_result = agent.run(goal, &working_dir).await;
    println!("Agent run finished. Result: {:?}", agent_output_result);

    // --- Assertions ---
    println!("Checking mock 1 hits...");
    api_mock_1.assert_hits(1);
    println!("Checking mock 2 hits...");
    api_mock_2.assert_hits(1);

    assert!(
        agent_output_result.is_ok(),
        "Agent run failed: {:?}",
        agent_output_result.err()
    );
    let agent_output = agent_output_result.unwrap();

    let calls = mock_provider.call_log.lock().unwrap();
    assert_eq!(calls.len(), 1);
    assert_eq!(calls[0].0, tool_name);
    assert_eq!(calls[0].1, tool_args.to_string());

    // Removed assertion for suggested_summary
    // assert!(agent_output.suggested_summary.is_some()); 
    assert_eq!(agent_output.applied_tool_results.len(), 1);
    let tool_result = &agent_output.applied_tool_results[0];
    assert_eq!(tool_result.tool_call_id, tool_call_id);
    assert_eq!(tool_result.tool_name, tool_name);
    assert_eq!(tool_result.input, tool_args);
    assert_eq!(tool_result.output, tool_output_content);
    assert_eq!(tool_result.status, ToolExecutionStatus::Success);
    assert_eq!(
        agent_output.final_state_description,
        Some(final_answer.to_string())
    );

    Ok(())
}

// TODO: More tests
"}

{"content":"// volition-cli/src/main.rs
mod models;
mod rendering;
mod tools;

use anyhow::{anyhow, Context, Result};
use colored::*;
use std::{
    env,
    fs,
    io::{self, Write},
    path::{Path, PathBuf},
};
use tokio::time::Duration;

use volition_agent_core::{
    // Removed unused ModelConfig import
    config::{RuntimeConfig}, 
    models::chat::ChatMessage,
    ToolProvider,
    Agent,
};

use crate::models::cli::Cli;
use crate::rendering::print_formatted;
use crate::tools::CliToolProvider;

use clap::Parser;
use reqwest::Client;
use std::sync::Arc;
use tracing::{error, info, warn, Level};
use tracing_subscriber::FmtSubscriber;

const CONFIG_FILENAME: &str = "Volition.toml";
const RECOVERY_FILE_PATH: &str = ".conversation_state.json";

// --- Configuration Loading ---

fn find_project_root() -> Result<PathBuf> {
    let current_dir = env::current_dir().context("Failed to get current directory")?;
    let mut current = current_dir.as_path();
    loop {
        let config_path = current.join(CONFIG_FILENAME);
        if config_path.exists() && config_path.is_file() {
            info!("Found configuration file at: {:?}", config_path);
            return Ok(current.to_path_buf());
        }
        match current.parent() {
            Some(parent) => current = parent,
            None => {
                return Err(anyhow!(
                    "Could not find '{}' in current directory or any parent directory.",
                    CONFIG_FILENAME
                ));
            }
        }
    }
}

fn load_cli_config() -> Result<(RuntimeConfig, PathBuf)> {
    let project_root = find_project_root()?;
    let config_path = project_root.join(CONFIG_FILENAME);
    let config_toml_content = fs::read_to_string(&config_path).with_context(|| {
        format!("Failed to read project config file: {:?}", config_path)
    })?;
    let api_key = env::var("API_KEY")
        .context("Failed to read API_KEY environment variable. Please ensure it is set.")?;

    let runtime_config = RuntimeConfig::from_toml_str(&config_toml_content, api_key)
        .context("Failed to parse or validate configuration content")?;

    Ok((runtime_config, project_root))
}

// --- Session Management ---

fn print_welcome_message() {
    println!("\n{}", "Volition - AI Assistant".cyan().bold());
    println!(
        "{}",
        "Type \'exit\' or press Enter on an empty line to quit.".cyan()
    );
    println!();
}

fn load_or_initialize_session(
    config: &RuntimeConfig,
    project_root: &Path,
) -> Result<Option<(Vec<ChatMessage>, String)>> {
    let recovery_path = project_root.join(RECOVERY_FILE_PATH);
    let mut messages_option: Option<Vec<ChatMessage>> = None;
    let initial_goal: Option<String>;

    if recovery_path.exists() {
        info!("Found existing session state file: {:?}", recovery_path);
        print!(
            "{}",
            "An incomplete session state was found. Resume? (Y/n): "
                .yellow()
                .bold()
        );
        io::stdout().flush()?;
        let mut user_choice = String::new();
        io::stdin().read_line(&mut user_choice)?;

        if user_choice.trim().to_lowercase() != "n" {
            match fs::read_to_string(&recovery_path) {
                Ok(state_json) => match serde_json::from_str(&state_json) {
                    Ok(loaded_messages) => {
                        messages_option = Some(loaded_messages);
                        info!("Successfully resumed session from state file.");
                        println!("{}", "Resuming previous session...".cyan());
                        let _ = fs::remove_file(&recovery_path);
                    }
                    Err(e) => {
                        error!("Failed to deserialize state file: {}. Starting fresh.", e);
                        println!("{}", "Error reading state file. Starting fresh.".red());
                        let _ = fs::remove_file(&recovery_path);
                    }
                },
                Err(e) => {
                    error!("Failed to read state file: {}. Starting fresh.", e);
                    println!("{}", "Error reading state file. Starting fresh.".red());
                    let _ = fs::remove_file(&recovery_path);
                }
            }
        } else {
            info!("User chose not to resume. Starting fresh.");
            println!("{}", "Starting a fresh session.".cyan());
            let _ = fs::remove_file(&recovery_path);
        }
    }

    if messages_option.is_none() {
        println!("{}", "How can I help you?".cyan());
        print!("{} ", ">".green().bold());
        io::stdout().flush()?;
        let mut initial_input = String::new();
        io::stdin().read_line(&mut initial_input)?;
        let trimmed_input = initial_input.trim();

        if trimmed_input.is_empty() || trimmed_input.to_lowercase() == "exit" {
            return Ok(None);
        }
        initial_goal = Some(trimmed_input.to_string());
        messages_option = Some(vec![ChatMessage {
            role: "system".to_string(),
            content: Some(config.system_prompt.clone()),
            ..Default::default()
        }]);
    } else {
        println!(
            "{}",
            "What is the main goal for this resumed session?".cyan()
        );
        print!("{} ", ">".green().bold());
        io::stdout().flush()?;
        let mut goal_input = String::new();
        io::stdin().read_line(&mut goal_input)?;
        let trimmed_input = goal_input.trim();
        if trimmed_input.is_empty() {
            println!("{}", "Goal cannot be empty. Exiting.".red());
            return Ok(None);
        }
        initial_goal = Some(trimmed_input.to_string());
    }

    if let (Some(messages), Some(goal)) = (messages_option, initial_goal) {
        Ok(Some((messages, goal)))
    } else {
        error!("Failed to establish initial messages and goal.");
        Ok(None)
    }
}

// --- Agent Execution ---

async fn run_agent_session(
    config: &RuntimeConfig,
    _client: &Client,
    tool_provider: Arc<dyn ToolProvider>,
    _initial_messages: Vec<ChatMessage>,
    initial_goal: String,
    working_dir: &Path,
) -> Result<()> {
    let agent = Agent::new(config.clone(), Arc::clone(&tool_provider))
        .context("Failed to create agent instance")?;

    info!("Starting agent run with goal: {}", initial_goal);

    match agent.run(&initial_goal, working_dir).await {
        Ok(agent_output) => {
            info!("Agent run finished successfully.");
            println!("\n{}", "--- Agent Run Summary ---".bold());

            if !agent_output.applied_tool_results.is_empty() {
                println!("\n{}:", "Tool Execution Results".cyan());
                for result in agent_output.applied_tool_results {
                    let status_color = match result.status {
                        volition_agent_core::ToolExecutionStatus::Success => "Success".green(),
                        volition_agent_core::ToolExecutionStatus::Failure => "Failure".red(),
                    };
                    println!(
                        "- Tool: {}, Status: {}",
                        result.tool_name.yellow(),
                        status_color
                    );
                }
            }

            if let Some(final_desc) = agent_output.final_state_description {
                println!("\n{}:", "Final AI Message".cyan());
                if let Err(e) = print_formatted(&final_desc) {
                    error!(
                        "Failed to render final AI message markdown: {}. Printing raw.",
                        e
                    );
                    println!("{}", final_desc);
                } else {
                    println!();
                }
            }
            println!("-----------------------\n");
        }
        Err(e) => {
            error!("Agent run failed: {:?}", e);
            println!("{}", "Agent run encountered an error:".red());
            println!("{:?}", e);
            return Err(e);
        }
    }
    cleanup_session_state(working_dir)
}

// --- Cleanup ---

fn cleanup_session_state(project_root: &Path) -> Result<()> {
    let recovery_path = project_root.join(RECOVERY_FILE_PATH);
    if recovery_path.exists() {
        if let Err(e) = fs::remove_file(&recovery_path) {
            warn!("Failed to remove recovery state file on exit: {}", e);
        } else {
            info!("Removed recovery state file on clean exit.");
        }
    }
    Ok(())
}

// --- Main Application Entry Point ---

#[tokio::main]
async fn main() -> Result<()> {
    dotenvy::dotenv().ok();
    let cli = Cli::parse();

    let level = match cli.verbose {
        0 => Level::WARN,
        1 => Level::INFO,
        2 => Level::DEBUG,
        _ => Level::TRACE,
    };
    let subscriber = FmtSubscriber::builder().with_max_level(level).finish();
    tracing::subscriber::set_global_default(subscriber).expect("setting default subscriber failed");

    let (config, project_root) = load_cli_config()
        .context("Failed to load configuration and find project root")?;

    let client = Client::builder()
        .timeout(Duration::from_secs(60))
        .build()
        .context("Failed to build HTTP client")?;

    let tool_provider = Arc::new(CliToolProvider::new());

    print_welcome_message();

    match load_or_initialize_session(&config, &project_root)? {
        Some((initial_messages, initial_goal)) => {
            if let Err(_e) = run_agent_session(
                &config,
                &client,
                tool_provider,
                initial_messages,
                initial_goal,
                &project_root,
            )
            .await
            {
                std::process::exit(1);
            }
        }
        None => {
            println!("\n{}", "Goodbye!".cyan());
        }
    }

    Ok(())
}
